    private void login_btActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_login_btActionPerformed
        // TODO add your handling code here:
        try{
       String uname=txt_name.getText();
       char[] pwd=txt_pass.getPassword();
       String password=new String(pwd);
      if((uname.equals("admin")&&(password.equals("1234")))){
          JOptionPane.showMessageDialog(null," SUCCESSFULLY  LOGGED  IN ");
      dispose();    
      home page=new home();
      page.setVisible(true);
      }
      else{
          JOptionPane.showMessageDialog(null,"  InValid  UserName  &  Password");
      }
   }catch(Exception e){
       System.out.println(e.getMessage());
   }
    }//GEN-LAST:event_login_btActionPerformed

