    private void show_password_check_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_show_password_check_buttonActionPerformed
        // TODO add your handling code here:
        if(show_password_check_button.isSelected()){
            txt_pass.setEchoChar((char)0);

        }
        else{
            txt_pass.setEchoChar('*');
        }
    }//GEN-LAST:event_show_password_check_buttonActionPerformed

